from rest_framework import serializersfrom core.serializers.base_serializer import BaseSerializerclass SupplierSerializer(BaseSerializer):    name = serializers.CharField(allow_null=True, allow_blank=True)    address = serializers.CharField(allow_null=True, allow_blank=True)    logo_url = serializers.URLField(allow_null=True, allow_blank=True)    longitude = serializers.FloatField()    dropoff_instructions = serializers.CharField(allow_null=True, allow_blank=True)    location_type = serializers.CharField(allow_null=True, allow_blank=True)